import React from 'react';
import './About.css';

const About = () => {
  return (
    <section className="about-section">
      <h2>About Me</h2>
      <p>
        I'm a Full Stack Developer with a passion for building scalable and responsive web applications.
        I specialize in MERN stack development — MongoDB, Express.js, React.js, and Node.js.
      </p>
      <p>
        I hold an M.Sc. in Computer Science from Prof. Ramkrishna More College and have completed internships
        where I contributed to full-stack projects using React, Redux, and RESTful APIs.
      </p>
    </section>
  );
};

export default About;
